import React, { useState } from 'react';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

interface CalculationResult {
  result: number;
  operation: string;
}

function App() {
  const [a, setA] = useState<string>('');
  const [b, setB] = useState<string>('');
  const [result, setResult] = useState<number | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState<boolean>(false);

  const performCalculation = async (endpoint: string, isUnary: boolean = false) => {
    setError(null);
    setResult(null);
    setLoading(true);

    try {
      const numA = parseFloat(a);
      if (isNaN(numA)) {
        setError('Please enter a valid number for A');
        setLoading(false);
        return;
      }

      const body = isUnary 
        ? { a: numA }
        : { a: numA, b: parseFloat(b) || 0 };

      if (!isUnary && isNaN(parseFloat(b))) {
        setError('Please enter a valid number for B');
        setLoading(false);
        return;
      }

      const response = await fetch(`${API_BASE_URL}${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(body),
      });

      const data: CalculationResult | { error: string } = await response.json();

      if (!response.ok || 'error' in data) {
        setError('error' in data ? data.error : 'An error occurred');
        setResult(null);
      } else {
        setResult(data.result);
        setError(null);
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Network error occurred');
      setResult(null);
    } finally {
      setLoading(false);
    }
  };

  const handleAdd = () => performCalculation('/api/v1/calculate/add');
  const handleSubtract = () => performCalculation('/api/v1/calculate/subtract');
  const handleMultiply = () => performCalculation('/api/v1/calculate/multiply');
  const handleDivide = () => performCalculation('/api/v1/calculate/divide');
  const handlePower = () => performCalculation('/api/v1/calculate/power');
  const handleSqrt = () => performCalculation('/api/v1/calculate/sqrt', true);
  const handlePercentage = () => performCalculation('/api/v1/calculate/percentage');

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl shadow-2xl p-8 w-full max-w-md">
        <h1 className="text-3xl font-bold text-center text-gray-800 mb-8">
          CalcAPI Pro
        </h1>

        <div className="space-y-4">
          <div>
            <label htmlFor="inputA" className="block text-sm font-medium text-gray-700 mb-2">
              Number A
            </label>
            <input
              id="inputA"
              type="number"
              value={a}
              onChange={(e) => setA(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
              placeholder="Enter number A"
            />
          </div>

          <div>
            <label htmlFor="inputB" className="block text-sm font-medium text-gray-700 mb-2">
              Number B (optional for sqrt)
            </label>
            <input
              id="inputB"
              type="number"
              value={b}
              onChange={(e) => setB(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
              placeholder="Enter number B"
            />
          </div>

          <div className="grid grid-cols-2 gap-3">
            <button
              onClick={handleAdd}
              disabled={loading}
              className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            >
              Add (+)
            </button>
            <button
              onClick={handleSubtract}
              disabled={loading}
              className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            >
              Subtract (-)
            </button>
            <button
              onClick={handleMultiply}
              disabled={loading}
              className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            >
              Multiply (×)
            </button>
            <button
              onClick={handleDivide}
              disabled={loading}
              className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            >
              Divide (÷)
            </button>
            <button
              onClick={handlePower}
              disabled={loading}
              className="px-4 py-2 bg-indigo-500 text-white rounded-lg hover:bg-indigo-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            >
              Power (a^b)
            </button>
            <button
              onClick={handleSqrt}
              disabled={loading}
              className="px-4 py-2 bg-indigo-500 text-white rounded-lg hover:bg-indigo-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            >
              Square Root (√)
            </button>
            <button
              onClick={handlePercentage}
              disabled={loading}
              className="px-4 py-2 bg-purple-500 text-white rounded-lg hover:bg-purple-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors col-span-2"
            >
              Percentage (a% of b)
            </button>
          </div>

          {loading && (
            <div className="text-center text-gray-600">Calculating...</div>
          )}

          {error && (
            <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg">
              {error}
            </div>
          )}

          {result !== null && !error && (
            <div className="bg-green-50 border border-green-200 text-green-800 px-4 py-3 rounded-lg text-center">
              <div className="text-sm font-medium mb-1">Result:</div>
              <div className="text-2xl font-bold">{result}</div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
